import React, {Component} from 'react';
import './PageTwo.css' ;

interface iprops{
  album:number
  count:number
  strength:number
  album_one:string
  album_one_photo_one:number
  album_one_photo_two:number
  album_one_photo_three:number
}

var count=1,strength=3

export default class Albumpage extends Component<{},any> {

 constructor (props:iprops){
   super(props)

   this.state={
     album:0,
     album_one: 'none',
     album_one_photo_one:0,
     album_one_photo_two:0,
     album_one_photo_three:0
   }

   this.tnailOne = this.tnailOne.bind(this);
   this.forward = this.forward.bind(this);
   this.backward = this.backward.bind(this);
   this.keyEvent = this.keyEvent.bind(this)
 }
tnailOne(){  
  this.setState({
    album:1,
    album_one: 'flex',
    album_one_photo_one:1,
    album_one_photo_two:0,
    album_one_photo_three:0
  });
}

forward(){
  switch(this.state.album){
     case 1:
       if(this.state.album_one_photo_one===1){
       count++;
       this.setState({album_one_photo_one:0,album_one_photo_two:1,album_one_photo_three:0});
       } else if (this.state.album_one_photo_two===1) {
       count++;
        this.setState({album_one_photo_one:0,album_one_photo_two:0,album_one_photo_three:1});
       } else if (this.state.album_one_photo_three===1){
         alert('On the Last Slide!');
       }
  }
}
backward(){

  switch(this.state.album){
     case 1:
       if(this.state.album_one_photo_one===1){
       alert('On the First Slide')
       } else if (this.state.album_one_photo_two===1) {
       count--;
        this.setState({album_one_photo_one:1,album_one_photo_two:0,album_one_photo_three:0});
       } else if (this.state.album_one_photo_three===1){
         count--
        this.setState({album_one_photo_one:0,album_one_photo_two:1,album_one_photo_three:0});
      }
  }
}

keyEvent(){
   this.forward();
}



render(){

   return(   

    <div className = 'disp'>

        <img className = 'hdr' src = {require('./images/hdr.jpg')} alt = "Nandana in Maize Field"/>

        <p className = 'txt_one'>My Photo Albums</p>

        <div className = 'lne_a'></div>

        <div className = 'cont_a'>

        <figure>
            <img className = 'img_hvr' onClick = {this.tnailOne} src = {require('./images/Album-1/01.jpg')} alt = 'Baby Nandana Album' />
            <figcaption className = "cption">Those early days...</figcaption>
        </figure>

        </div>

        <div className = 'albm' tabIndex={-1} onKeyDown={this.keyEvent} style = {{display:this.state.album_one}} >
         <i className = 'material-icons left_icn' onClick = {this.backward}>chevron_left</i>
         <img className = 'photo' style={{opacity:this.state.album_one_photo_one}}   src = {require('./images/Album-1/01.jpg') } alt ="baby Nandana sleepsuit"/>
         <img className = 'photo' style={{opacity:this.state.album_one_photo_two}}   src = {require('./images/Album-1/02.jpg') } alt ="baby Nandana Photoshoot"/>
         <img className = 'photo' style={{opacity:this.state.album_one_photo_three}} src = {require('./images/Album-1/03.jpg') } alt ="baby Nandana Garden"/>
         <i className = 'material-icons right_icn' onClick={this.forward}>chevron_right</i>
         <i className = 'material-icons close_icn'>close</i>
         <p className = 'albm_ftr'>{count} / {strength}</p>
        </div>


    </div>
  )
}

}
